def loadTexture(name):
    texture = Image.open(name)
    textureData = texture.convert('RGB').tobytes()
    width = texture.width
    height = texture.height
    texturas.append([textureData, width, height])

def loadTextureOpenGL(textureData, width, height):
    glEnable(GL_TEXTURE_2D)
    texid = glGenTextures(1)
    glBindTexture(GL_TEXTURE_2D, texid)
    glTexImage2D(GL_TEXTURE_2D, 0, GL_RGB, width, height,
                 0, GL_RGB, GL_UNSIGNED_BYTE, textureData)
    glTexParameteri(GL_TEXTURE_2D, GL_TEXTURE_WRAP_S, GL_REPEAT)
    glTexParameteri(GL_TEXTURE_2D, GL_TEXTURE_WRAP_T, GL_REPEAT)
    glTexParameteri(GL_TEXTURE_2D, GL_TEXTURE_MAG_FILTER, GL_NEAREST)
    glTexParameteri(GL_TEXTURE_2D, GL_TEXTURE_MIN_FILTER, GL_LINEAR)
    glTexEnvf(GL_TEXTURE_ENV, GL_TEXTURE_ENV_MODE, GL_DECAL)
    return texid

def squad(x, y, z, n, i, c):
    loadTextureOpenGL(c[0], c[1], c[2])
    glBegin(GL_QUADS)
    if i == 0:
        glTexCoord2f(0.0, 0.0)
        glVertex3f(x, y, z)
        glTexCoord2f(1.0, 0.0)
        glVertex3f(x + n, y, z)
        glTexCoord2f(1.0, 1.0)
        glVertex3f(x + n, y - n, z)
        glTexCoord2f(0.0, 1.0)
        glVertex3f(x, y - n, z)
        ab = np.array([x + n, y, z]) - np.array([x, y , z])
        ac = np.array([x + n, y - n, z]) - np.array([x, y, z])
        normal = np.cross(ab, ac)
        glNormal3fv(normal)
    elif i == 1:
        glTexCoord2f(0.0, 0.0)
        glVertex3f(x, y, z)
        glTexCoord2f(1.0, 0.0)
        glVertex3f(x, y, z - n)
        glTexCoord2f(1.0, 1.0)
        glVertex3f(x, y - n, z - n)
        glTexCoord2f(0.0, 1.0)
        glVertex3f(x, y - n, z)
        ab = np.array([x, y, z - n]) - np.array([x, y , z])
        ac = np.array([x, y - n, z - n]) - np.array([x, y, z])
        normal = np.cross(ab, ac)
        glNormal3fv(normal)
    elif i == 2:
        glTexCoord2f(0.0, 0.0)
        glVertex3f(x, y, z)
        glTexCoord2f(1.0, 0.0)
        glVertex3f(x + n, y, z)
        glTexCoord2f(1.0, 1.0)
        glVertex3f(x + n, y, z - n)
        glTexCoord2f(0.0, 1.0)
        glVertex3f(x, y, z - n)
        ab = np.array([x + n, y, z]) - np.array([x, y , z])
        ac = np.array([x + n, y, z - n]) - np.array([x, y, z])
        normal = np.cross(ab, ac)
        glNormal3fv(normal)
    glEnd()

def cubo(x, y, z, c):
    n = 1/10
    squad(x, y, z, n, 0, c[0])
    squad(x, y, z-n, n, 0, c[1])
    squad(x + n, y, z, n, 1, c[2])
    squad(x, y, z, n, 1, c[3])
    squad(x, y - n, z, n, 2, c[4])
    squad(x, y, z, n, 2, c[5])

