def showScreen():
    glClear(GL_COLOR_BUFFER_BIT | GL_DEPTH_BUFFER_BIT)
    glLoadIdentity()
    iterate()
    # gluPerspective(120, 1, 1, 40)
    glPushMatrix()
    suma = np.array(cameraPos + cameraFront)
    gluLookAt(cameraPos[0], cameraPos[1], cameraPos[2], suma[0], suma[1], suma[2],
              cameraUp[0], cameraUp[1], cameraUp[2])    # gluLookAt(5, 5, 5, px, py, pz, 0, 0, 1)
    make_lab()
    eje()
    glPopMatrix()
    glutSwapBuffers()

def main():
    glutInit()  # Iniciamos la instancia de glut
    # Asignamos el modelo de color que usaremos
    glutInitDisplayMode(GLUT_RGB | GLUT_DOUBLE | GLUT_DEPTH)
    # Damos el tamaño de la ventana que se mostrará
    glutInitWindowSize(600, 600)
    # Coordenadas en donde aparecerá la ventana en la pantalla
    glutInitWindowPosition(0, 0)
    window = glutCreateWindow("Laberinto en 3D")  # Damos un titulo para la ventana
    glEnable(GL_DEPTH_TEST)  # Permite ver sin transparentar las figuras
    glutDisplayFunc(showScreen)
    glutKeyboardFunc(move)
    glutMouseFunc(move_mouse)
    glutMotionFunc(calc_move_mouse)
    glutMainLoop()  # Iniciamos el loop principal


main()
